<style lang="scss">
    .eg-icon {
        font-size: inherit;
        display: inline-block;
        vertical-align: middle;
        transition: opacity .2s, transform .2s;
        transform-origin: center;
        svg {
            height: 100%;
        }
        .material-icons {
            font-size: inherit;
        }
        &.clickable {
            &:hover {
                opacity: 0.5;
                cursor: pointer;
            }
        }
        & > * {
            transition: transform .2s;
        }
        &.reversed > * {
            transform: rotate(-180deg)!important;
        }
    }
</style>

