<style lang="scss">
    .svg-icon {
        display: inline-block;
        user-select: none;
        margin: 0;
        min-height: 1em;
        min-width: 1em;
        svg {
            min-height: inherit;
            min-width: inherit;
        }
        &.small {
            width: 20px;
            height: 20px;
            svg {
                height: inherit;
                width: inherit;
            }
        }
        &.clickable {
            cursor: pointer;
            transition: opacity .2s;
            &:hover {
                opacity: 0.5;
            }
        }
        &.fr {
            float: right;
        }
        &.fl {
            float: left;
        }
        .material-icons {
            font-size: inherit;
        }
    }
</style>

